import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Calendar, Views, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import { useLocation } from "react-router-dom";
//styled components
const Container = styled.div`
  width: 100vw;
  height: 90vh;
  background-color: cornflowerblue;
  display: flex;
`;
const Header = styled.div`
  width: 100vw;
  height: 10vh;
  background-color: cornflowerblue;
  display: flex;
  align-items: flex-end;
  justify-content: center;
`;
const TitleBar = styled.h2`
  font-size: 40pt;
  color: white;
  font-weight: bolder;
`;
const Main = styled.div`
  margin: 20px;
  width: 100%;
  background-color: white;
`;
const SideBar = styled.div`
  width: 40%;
  background-color: cornflowerblue;
  display: flex;
  align-items: stretch;
`;
const EventPanel = styled.div`
  margin: 20px;
  background-color: lightgrey;
  width: 100%;
  border-radius: 5px;
  padding: 8px;
`;
let allViews = Object.keys(Views).map(k => Views[k]);
const localizer = momentLocalizer(moment);
const defaultState = {
  events: [
    {
      id: 1,
      start: new Date(),
      end: new Date(moment().add(1, "days")),
      title: "Event 1",
      color: "lightblue"
    },
    {
      id: 2,
      start: new Date(),
      end: new Date(moment().add(3, "days")),
      title: "Event 2",
      color: "lightblue"
    }
  ]
};
const selectEvent = e => {
  console.log(e);
};
async function fetchUser(token) {
  let promise = await fetch(`https://www.eventbriteapi.com/v3/users/me/`, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return await promise.json();
}
async function fetchOrgs(id, token) {
  let promise = await fetch(
    `https://www.eventbriteapi.com/v3/users/${id}/organizations/?token=${token}`
  );
  return await promise.json();
}

async function fetchEvents(orgIds, token) {
  let urls = orgIds.map(id =>
    fetch(
      `https://www.eventbriteapi.com/v3/organizations/${id}/events/?token=${token}`
    )
  );
  Promise.all(urls).then(promises => {
    Promise.all(promises.map(res => res.json())).then(async data => await data);
  });
}

function EventTool() {
  let { hash } = useLocation();
  let [events, setEvents] = useState([]);
  let token = hash.split("=")[2];
  useEffect(() => {
    async function apiCalls(key) {
      let userData = await fetchUser(key);
      let orgData = await fetchOrgs(userData.id, key);
      let orgIds = orgData.organizations.map(org => org.id);
      async function fetchEvents(orgIds, token) {
        let urls = orgIds.map(id =>
          fetch(
            `https://www.eventbriteapi.com/v3/organizations/${id}/events/?token=${token}`
          )
        );
        Promise.all(urls).then(promises => {
          Promise.all(promises.map(res => res.json())).then(data => {
            let events = [];
            data.forEach(array => {
              events = events.concat(array.events);
              events = events.map(event => {
                return {
                  ...event,
                  title: event.name.text
                };
              });
            });
            setEvent(events);
          });
        });
      }
      fetchEvents(orgIds, key);
    }
    apiCalls(token);
  }, []);
