import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Calendar, Views, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import { useParams } from "react-router-dom";
//styled components
const Container = styled.div`
  width: 100vw;
  height: 90vh;
  background-color: cornflowerblue;
  display: flex;
`;
const Header = styled.div`
  width: 100vw;
  height: 10vh;
  background-color: cornflowerblue;
  display: flex;
  align-items: flex-end;
  justify-content: center;
`;
const TitleBar = styled.h2`
  font-size: 40pt;
  color: white;
  font-weight: bolder;
`;
const Main = styled.div`
  margin: 20px;
  width: 100%;
  background-color: white;
`;
const SideBar = styled.div`
  width: 40%;
  background-color: cornflowerblue;
  display: flex;
  align-items: stretch;
`;
const EventPanel = styled.div`
  margin: 20px;
  background-color: lightgrey;
  width: 100%;
  border-radius: 5px;
  padding: 8px;
`;
let allViews = Object.keys(Views).map(k => Views[k]);
const localizer = momentLocalizer(moment);
const defaultState = {
  events: [
    {
      id: 1,
      start: new Date(),
      end: new Date(moment().add(1, "days")),
      title: "Event 1",
      color: "lightblue"
    },
    {
      id: 2,
      start: new Date(),
      end: new Date(moment().add(3, "days")),
      title: "Event 2",
      color: "lightblue"
    }
  ]
};
const selectEvent = e => {
  console.log(e);
};
async function fetchWeather() {
  let promise = await fetch(`https://www.eventbriteapi.com/v3/users/me/`, {
    headers: { Authorization: "Bearer PERSONAL_OAUTH_TOKEN" }
  });
  return await promise.json();
}

async function authenticate() {
  const apikey = "2HFMBE2GUW52R2FYGTBQ";
  let promise = await fetch(
    `https://www.eventbrite.com/oauth/authorize?response_type=token&client_id=${apikey}`
  );
  return await promise.json();
}

function EventTool() {
  let [weather, setWeather] = useState({});
  useParams
  useEffect(() => {
    async function weather() {
      let data = await authenticate();
      setWeather(data);
    }
    weather();
  }, []);
  console.log(weather);
  return (
    <>
      <Header>
        <TitleBar>Event Tool</TitleBar>
      </Header>
      <Container>
        <Main>
          <Calendar
            localizer={localizer}
            defaultDate={new Date()}
            showMultiDayTimes
            step={60}
            views={allViews}
            defaultView="month"
            events={defaultState.events}
            onSelectEvent={selectEvent}
            style={{ borderRadius: "5px" }}
          />
        </Main>
        <SideBar>
          <EventPanel>test</EventPanel>
        </SideBar>
      </Container>
    </>
  );
}

export default EventTool;
