import React from "react";
import styled from "styled-components";
import { Calendar, ViewsmomentLocalizer } from "react-big-calendar";
import moment from "moment";
//styled components
const Container = styled.div`
  width: 100vw;
  height: 90vh;
  background-color: cornflowerblue;
  display: flex;
`;
const Header = styled.div`
  width: 100vw;
  height: 10vh;
  background-color: cornflowerblue;
  display: flex;
  align-items: flex-end;
  justify-content: center;
`;
const TitleBar = styled.h2`
  font-size: 40pt;
  color: white;
  font-weight: bolder;
`;
const Main = styled.div`
  margin: 20px;
  width: 100%;
  background-color: white;
`;
const SideBar = styled.div`
  width: 40%;
  background-color: cornflowerblue;
  display: flex;
  align-items: stretch;
`;
const EventPanel = styled.div`
  margin: 20px;
  background-color: lightgrey;
  width: 100%;
  border-radius: 5px;
  padding: 8px;
`;
const localizer = momentLocalizer(moment);
const defaultState = {
  events: [
    {
      id: 1,
      start: new Date(),
      end: new Date(moment().add(1, "days")),
      title: "Event 1"
    }
  ]
};
const selectEvent = e => {
  console.log(e);
};

function EventTool() {
  return (
    <>
      <Header>
        <TitleBar>Event Tool</TitleBar>
      </Header>
      <Container>
        <Main>
          <Calendar
            localizer={localizer}
            defaultDate={new Date()}
            defaultView="month"
            events={defaultState.events}
            onSelectEvent={selectEvent}
            style={{ borderRadius: "5px" }}
          />
        </Main>
        <SideBar>
          <EventPanel>test</EventPanel>
        </SideBar>
      </Container>
    </>
  );
}

export default EventTool;
